template.value:
// Breakpoints (values are calculated assuming default browser font is 16px; therefore, {WANTED}px/16px = {RESULT}em since media queries are not affected by root font size)
$breakpoints: (
  // 600px
    mobile: 37.5em,
  // 900px
    tablet: 56.25em,
  // 1200px
    tablet-large: 75em,
  // 1800px
    desktop-large: 112.5em
);

@function breakPoint($key) {
  @return map-get($breakpoints, $key);
}

// Media Query Manager
// (0px,600px]: Phone
// (600px,900px]: Tablet
// (900,1200px]: Tablet Large
// (1200px, 1800px]: Desktop
// (1800,+infinity): Desktop large
// ORDER: base + typography > general layout + grid > page layout > components

// max-width: desktop 1st design
@mixin mediaQuery($bp) {
  @if $bp != desktop-large {
    @media only screen and (max-width: breakPoint($bp)) {
      @content;
    }
  }

  @if $bp == desktop-large {
    @media only screen and (min-width: breakPoint($bp)) {
      @content;
    }
  }
}
